import { Component } from '@angular/core';
import { DataService } from './data.service';
import { Observable, shareReplay, BehaviorSubject } from 'rxjs';

@Component({
    selector: 'app-header',
    templateUrl: 'header.component.html',
    providers: [ DataService ]
})

export class HeaderComponent {

    usersStorage: object = [];

    constructor(public dataService: DataService) {}

    userName : any;
    userID : any;

    ngOnInit(){
        this.dataService.getUserStorage().subscribe((res)=>{
            let _userData = JSON.stringify(res);
            let userData = JSON.parse(_userData) || [];
            this.usersStorage = userData;
        })

        this.dataService.curUserFullName.subscribe((res) => this.userName = res);
        this.dataService.curUserID.subscribe((res) => this.userID = res);
    }

    ngOnDestroy(){
        this.dataService.curUserFullName.unsubscribe();
        this.dataService.curUserID.unsubscribe();
    }

}
