import { Component } from '@angular/core';
import { DataService } from './data.service';
import { CookieService } from 'ngx-cookie-service';
import { count } from 'rxjs';

@Component({
    selector: 'app-news',
    templateUrl: 'news.component.html'
})

export class NewsComponent {
    usersStorage: any;
    userID : any = this.cookieService.get('userID'); 
    userPostsList : any;

    constructor(public dataService: DataService,
        private cookieService: CookieService) {}

    ngOnInit(){
        this.dataService.getUserStorage().subscribe((res)=>{
            let _userData = JSON.stringify(res);
            let userData = JSON.parse(_userData) || [];
            this.usersStorage = userData;

            console.log(userData[this.userID - 1]);

            let userPost = {
                "id": 0,
                "author": "",
                "text": ""
            }

            let countID = 1;

            for(l) friend of JSON.parse(userData[this.userID - 1].friends)){
                for(let post of JSON.parse(userData[friend-1].post)){
                    userPost.id = countID++;
                    userPost.author = userData[friend].name + userData[friend].surname;
                    userPost.text = post;
                    this.userPostsList.push(userPost);
                }          
            }

            let comp = ((a : any, b : any) => {
                return (b.id-a.id); 
            });

            this.userPostsList.sort(comp);

            console.log(this.userPostsList);
        })
    }
}

