import { Component } from '@angular/core';
import { DataService } from './data.service';
import { CookieService } from 'ngx-cookie-service';

@Component({
    selector: 'app-friends',
    templateUrl: 'friends.component.html'
})


export class FriendsComponent {
    usersStorage: any;
    userID : any = this.cookieService.get('userID'); 
    friendsIDs : any = [{"f": 1},{"f": 2}];

    constructor(public dataService: DataService,
        private cookieService: CookieService) {}

    ngOnInit(){
        this.dataService.getUserStorage().subscribe((res)=>{
            let _userData = JSON.stringify(res);
            let userData = JSON.parse(_userData) || [];
            this.usersStorage = userData;

            this.friendsIDs = JSON.parse(userData[this.userID].friends);
            console.log(this.friendsIDs);
        })
    }
}

<ul>
    <li *ngFor = "let _friend of friendsIDs; let i = index" class="lol">
        <p>{{_friend}}</p>
        <div style="background-color: aqua;">
            <img src = "/assets/images/avatar/{{_friend}}.png">
            <span>{{usersStorage[_friend].surname}}  {{usersStorage[_friend].name}}</span>
            <span>[admin]</span>
        </div>
    </li>
</ul>
