import { Component } from '@angular/core';
import { DataService } from './data.service';
import { CookieService } from 'ngx-cookie-service';

@Component({
    selector: 'app-messenger',
    templateUrl: 'messenger.component.html',
    styleUrls: ["friends.component.less"]
})


export class MessengerComponent {
    usersStorage: any;

    userDialogs: any = [{}];

    currentDialog : any = [{}]
    userID : any = this.cookieService.get('userID'); 

    friendList : any = [{}];

    constructor(public dataService: DataService,
        private cookieService: CookieService) {}

    ngOnInit(){
        this.dataService.getUserStorage().subscribe((res)=>{
            let _userData = JSON.stringify(res);
            let userData = JSON.parse(_userData) || [];
            this.usersStorage = userData;

            this.friendList.pop();
            this.userDialogs.pop();

            let friendsIDs = JSON.parse(userData[this.userID-1].friends);
            for(let i of friendsIDs){
                this.friendList.push(userData[i-1]);
            }

            for(let i of friendsIDs){             
                this.dataService.getUserDialog(this.userID, i).subscribe((res) => {
                    let friendDialog = {
                        "id": i,
                        "dialog": res 
                    } 
                    this.userDialogs.push(friendDialog);
                })
            }
        })
    }

    checkDialog(event : any){
        let eID = event.originalTarget.id;
        if(eID){
            this.currentDialog = this.userDialogs.find((obj : object) => {obj.id.toString() == eID});
            console.log(this.currentDialog);
        }
        console.log(eID);
    }




}

<div>
    <ul class="friend-list">
        <li *ngFor="let friend of friendList;" class="friend-list-elem">
            <div class="messenger-list-elem-cont" id="{{friend.id}}" (click)="checkDialog($event)">
                <img src="/assets/images/avatar/{{friend.id}}.png" class="friend-avatar">
                <span class="friend-text">{{friend.surname}} {{friend.name}}</span>
            </div>
        </li>
    </ul>
</div>
