import { Component } from '@angular/core';
import { DataService } from './data.service';
import { Data } from '@angular/router';

@Component({
    selector: 'app-register',
    templateUrl: 'register.component.html'
})

export class RegisterComponent {
    name: string = "";
    surname: string = "";
    patronymic: string = "";
    email: string = "";
    password: string = "";
    birthdate: string = "";

    comment: string = "";

    // "id": "1",
    // "name": "Егор",
    // "surname": "Шушков",
    // "patronymic": "Васильевич",
    // "email": "egorka44552014@gmail.com",
    // "password": "1234eee",
    // "birthdate": "2004-04-08",
    // "role": "admin",
    // "status": "active",
    // "friends": "[2,3,4,5]",
    // "post": "[\"Анекдо́т — фолькло

    usersStorage: any;

    constructor(public dataService: DataService) {}

    ngOnInit(){
        this.dataService.getUserStorage().subscribe((res)=>{
            let _userData = JSON.stringify(res);
            let userData = JSON.parse(_userData) || [];
            this.usersStorage = userData;
        })
    }

    registerUser(){
        console.log(this.birthdate);
    }


}
